--convert hex to RGB
function hex2rgb(hex)
    hex = hex:gsub("#","")
    return tonumber("0x"..hex:sub(1,2)), tonumber("0x"..hex:sub(3,4)), tonumber("0x"..hex:sub(5,6))
end

function GetConsoleColumnCount(localConsole)
--get average pilxelwidth of font local fontWidth, _ = calcFontSize(getFontSize()) --Get size of the entire window. local localConsoleWidth = localConsole:get_width()
--Calculate how many characters can fit into consoles localConsoleColumnCount = math.floor((localConsoleWidth / fontWidth) - 1)
displayDebug("function parthiaRoomCharactersWrap, Font Width: "..tostring(fontWidth) .." localConsoleWidth: "..tostring(localConsoleWidth) .." localConsoleColumnCount: "..tostring(localConsoleColumnCount))
return localConsoleColumnCount end --function parthiaRoomCharactersWrap()

