--load display settings. function UIManagerFileLoad(displayToScreen) local tecTMPDisplayTable = {}
local path = tecSettingsFolder.."TECUIManagerData.lua" path = path:gsub("\\","/") --If the file does not exist let the user know. if not io.exists(path) then debugToDisplay("tecFileLoadSettings: "..path.." file doesn't exist.") --TEC debug screen pecho("[ Info ] - No display settings file. Parthia using default settings.\n") --debugc("tecFileLoadSettings: "..path.." file doesn't exist.") --mudlets debug screen return --kill the function before real errors occur else table.load(path, tecTMPDisplayTable) --Load the settings in from file --if UIManagerSettings table does not exist make it. if not UIManagerSettings then UIManagerSettings = {} end --load settings into UIManagerSettings table. UIManagerSettings = table.update(UIManagerSettings, tecTMPDisplayTable) if displayToScreen then pecho("[ OK ] - Parthia UI Manager settings successfully loaded from file.\n") end --if displayToScreen end --end if file exists else
end --function UIManagerFileLoad

