function loadHelpDevs(displayToScreen) displayToScreen = fuzzyBoolean(displayToScreen) local tmpTable = {} --tmp table to hold data. local path = helpDevDataFile --name and folder of file
if not io.exists(path) then --If the file does not exist let the user know. tecWarningNotification("function loadHelpDevs, file "..path.." not found.") if displayToScreen then pecho("[ info ] - Help The Developers save file not found.\n") end --if displayToScreen return --kill the function before real errors occur else --file was found table.load(path, tmpTable) --Load the data in from file HelpDevsData = table.update(HelpDevsData, tmpTable) --update the existing HelpDevsData table incase load is run after startup if displayToScreen then pecho("[ OK ] - Help The Developers data successfully loaded from file.\n") end --if displayToScreen end --end if file exists else
end --function loadHelpDevs(displayToScreen)

--[[ Detects if combat action is against the player or if the player is running the action saves the action to the Help Developers table if the player has Help Developers enabled If a string is not passed ]]-- function sortCombatActions(stringToCheck) stringToCheck = stringTocheck or line --if user did not pass a string, check global line if type(stringToCheck) ~= "string" then --make certain a string was passed tecErrorNotification("function collectCombatActions, passed a non string variable. Received: "..tostring(stringToCheck).." expected a string.") end --if type(stringToCheck) ~= "string"
if stringToCheck:match("at you") or --if the attack is directed at the player. stringToCheck:match("misses you") or stringToCheck:match("towards you") or stringToCheck:match("slap you,") or stringToCheck:match("head butt you") or stringToCheck:match("tries to distract you") or stringToCheck:match("a knee into you") or stringToCheck:match("parthia playercombatdata") then if getHelpDevs() then --if Help Developers is enabled if not table.contains(HelpDevsData.Combat.AgainstPlayer, stringToCheck) then --if stringToCheck is not in the AgainstPlayer table debugToDisplay("function collectCombatActions, adding "..stringToCheck.." to HelpDevsData.Combat.AgainstPlayer table.") HelpDevsData.Combat.AgainstPlayer[stringToCheck] = "" --collect attack to table. end --if not table.contains(HelpDevsData.Combat.AgainstPlayer, stringTocheck) end --if getHelpDevs() elseif stringToCheck:match("%] You") or --if the attack is one the player is using stringToCheck:match(", you") then if getHelpDevs() then --if Help Developers is enabled if not table.contains(HelpDevsData.Combat.PlayerAttacks, stringToCheck) then --if stringToCheck is not in the HelpDevsData.Combat.PlayerAttacks table debugToDisplay("function collectCombatActions, adding "..stringToCheck.." to HelpDevsData.Combat.PlayerAttacks table.") HelpDevsData.Combat.PlayerAttacks[stringToCheck] = "" --collect attack to table. end --if not table.contains(HelpDevsData.Combat.PlayerAttacks, stringTocheck) end --getHelpDevs() else --we do not know where this attack belongs if getHelpDevs() then --if Help Developers is enabled if not table.contains(HelpDevsData.Combat.Unsorted, stringToCheck) then --if stringToCheck is not in the HelpDevsData.Combat.Unsorted table debugToDisplay("function collectCombatActions, adding "..stringToCheck.." to HelpDevsData.Combat.Unsorted table.") HelpDevsData.Combat.Unsorted[stringToCheck] = "" --collect attack to table. end --if not table.contains(HelpDevsData.Combat.Unsorted, stringTocheck) end --if getHelpDevs() then end --if stringToCheck:match sort combat types if elseif series
end --function sortCombatActions(stringToCheck)

function parthiaGetWaitForDrover()
  return parthiaWaitingForDrover 
