--message that the course has no more to offer comes after the room arrival message. This function prevents moving when it is no longer needed function courses.firstStep()
if courses.running then if courses.fourPart then send("go south") elseif courses.threePart then send("go east") end --if courses.fourPart end --if courses.running
end --function courses.threePartFirstStep()

--return if the stop command was run by scripts.
function parthiaGetStopRan()

  return parthiaStopRan

end --parthiaGetStopRan()

--[[ Used to suppress the "You aren't doing anything." message that appears when a player runs the stop command to stop scripts, but they are not doing a task in the game Used in trigger "You aren't doing anything." if the mode is true the message is suppressed. If not the message is shown. delay allows the player to run `stop` than delay for the "You aren't doing anything." message to arrive. Setting it to false after it arrives.
usage: parthisSetStopRun(mode, delay) mode is a boolean. true stops the "You aren't doing anything." false allows it to show. delay is a boolean. If true the function waits half a second to set the mode. ]]--
function parthisSetStopRun(mode, delay) mode = fuzzyBoolean(mode) delay = fuzzyBoolean(delay)
if delay then tempTimer(.5, [[parthisSetStopRun(true)]]) else parthiaStopRan = mode end --if delay
end --function parthisSetStopRun(mode, delay)

--stops all scripts running in parthia
function parthiaStopScripts(displayToScreen)

  if courses.running then courses.disable(displayToScreen) end --If running, disable courses scripts
  if parthiaGetWaitForDrover() then parthiaSetWaitForDrover(false, displayToScreen) end --if running, disable waiting for drover script

end --function parthiaStopScripts(displayToScreen)

--returns the enabled or disabled state of numpad navigation
function getNumpadNaviation()
  return tecSettings.numpadNavigationEnabled
end --function getNumpadNaviation()

--load player character locations from file.
function parthiaMap.load(displayToScreen)
  displayToScreen = fuzzyBoolean(displayToScreen)

  if io.exists(parthiaMap.savePath) then --if the file exists, load it.
    table.load(parthiaMap.savePath, parthiaMap.saveTable)
    if displayToScreen then pecho("[  OK  ]  - Parthia map settings successfully loaded from file.\n") end
  else --the save file does not exist
    if displayToScreen then pecho("[ Info ]  - No map settings file.\n") end
  end --if io.exists(parthiaMap.savePath)

end --function parthiaMap.load(displayToScreen)

--Save map settings to file
function parthiaMap.save(displayToScreen)
  displayToScreen = fuzzyBoolean(displayToScreen)
  parthiaTableSave(parthiaMap.saveTable, parthiaMap.savePath, false, displayToScreen)
end --function parthiaMap.save()

--[[
Used to check if parthia map is being used
Useage: 
if parthiaMap.getEnabled() then
  --code that requires mapper here
end
