function tecConfigThemes() --configure and initalize settings for tabs.
  --Default tab height
  tabsHeight = 30

  --QLabels are QTs label system
  --Used here for easy hover affects
  tabsStyle = [[
  	QLabel{
  		background-color: ]]..tecSettings.tabsBackgroundColor..[[;
      border-width: 1px;
      border-style: outset;
      border-color: lightgray;
      border-top-left-radius: 15px;
      border-top-right-radius: 15px;
      margin-right: 0px;
      margin-left: 0px;
  		font-size: ]]..tecSettings.tecTabsFontSize..[[px;
      qproperty-alignment: 'AlignCenter | AlignCenter';
    }
  	QLabel::hover{
  		background-color: ]]..tecSettings.hoverColor..[[;
      border-width: 1px;
      border-style: outset;
      border-color: gray;
      border-top-left-radius: 15px;
      border-top-right-radius: 15px;
      margin-right: 0px;
      margin-left: 0px;
  		font-size: ]]..tecSettings.tecTabsFontSize..[[px;
      qproperty-alignment: 'AlignCenter | AlignCenter';
    }
  ]]

  --This is now tabs text looks
  tabsFontStyle = [[<center><p style="font-size:]]
    ..tecSettings.tecTabsFontSize..[[px; color:lightgray">]]

labelsStyle = [[
	QLabel{
		background-color: ]]..tecSettings.labelBackgroundColor..[[;
    border-width: 1px;
    border-style: outset;
    border-color: lightgray;
    margin-right: 0px;
    margin-left: 0px;
		font-size: 20px;
    qproperty-alignment: 'AlignCenter | AlignCenter';
  }
	QLabel::hover{
		background-color: ]]..tecSettings.hoverColor..[[;
    border-width: 1px;
    border-style: outset;
    border-color: gray;
    margin-right: 0px;
    margin-left: 0px;
		font-size: 20px;
    qproperty-alignment: 'AlignCenter | AlignCenter';
  }
]]

buttonsStyle = [[QPushButton{
                		background-color: gray;
                    border-width: 1px;
                    border-style: outset;
                    border-color: lightgray;
                    border-top-left-radius: 15px;
                    border-top-right-radius: 15px;
										border-bottom-left-radius: 15px;
                    border-bottom-right-radius: 15px;
                    margin-right: 1px;
                    margin-left: 1px;
                		font-size: 50px;
                    qproperty-alignment: 'AlignCenter | AlignCenter';}
								 QPushButton::on{}
								 QPushButton::hover{}
]]					

--This is now tabs text looks
buttonsFontStyle = '<center><p style="font-size:18px; color:white">'

